import axios from "axios";
import React, {useState} from "react";
import { useNavigate } from "react-router-dom";

function AddProduct(){

    const navigate = useNavigate();
    const [formvalue, setFormvalue] = useState({nome:'',valor:''});
    const [message, setMessage] = useState('');
    const [types, setTypes] = useState('');
    const handleInput =(e)=>{
        setFormvalue({...formvalue, [e.target.name]:e.target.value});
    }

    async function getType() {
        try {
          const response = await axios.get('http://localhost:8080/tipos');
          console.log(response);
          setTypes(response.data.data);
        } catch (error) {
          console.error(error);
        }
      }

    /*var tipos = axios.get("http://localhost:8080/tipos");
    var tiposlist = tipos.data;
    console.log(tipos);*/

    const handleSubmit =async(e)=>{
        e.preventDefault();

        var bodyFormData = new FormData();
        bodyFormData.append('nome', formvalue.nome);
        bodyFormData.append('valor', formvalue.valor);

        const res = await axios.post("http://localhost:8080/cadastrar-produtos",bodyFormData);

        if(res.data.success){
            setMessage(res.data.data.success);
            setTimeout(
                ()=>{
                    navigate('/productlist')
                }, 2000
            );
        }
    }

    return(
        <React.Fragment>
        <div className="container">
            <h5>Adicionar Produto</h5>
            <p>{ message }</p>
            <form onSubmit={handleSubmit}>
                <div className="mb-3">
                    <label htmlFor="exampleInputEmail1" className="form-label">Nome do produto:</label>
                    <input type="text" name="nome" className="form-control" id="nome" value={formvalue.nome} onChange={handleInput}/>
                </div>
                <div className="mb-3">
                    <label htmlFor="exampleInputPassword1" className="form-label">Valor:</label>
                    <input type="text" name="valor" className="form-control" id="valor"value={formvalue.valor} onChange={handleInput}/>
                </div>
                <div className="mb-3">
                    <select class="form-select" aria-label="">
                        <option selected>Selecione um tipo:</option>
                        {
                            [types].map((tData, index)=>{
                                return (
                                    <option value={tData.id}>{tData.nome}</option>
                                );
                            })
                        }

                    </select>
                </div>
                <button type="submit" className="btn btn-primary">Submit</button>
            </form>
        </div>
        </React.Fragment>
    );
}

export default AddProduct;
