import React, {useEffect, useState} from "react";
import { Link } from "react-router-dom";
import axios from "axios";


function Sale(){

    const [carrinho, setCarrinho] = useState();

    function addProducToCart(id){
        const copyProductsCart = [...carrinho];

        copyProductsCart.push({id: id, qtd: 1});

        setCarrinho(copyProductsCart);
    }


    const [productData, setProductData] = useState([]);
    const [formvalue, setFormvalue] = useState([]);
    const handleInput =(e)=>{
        setFormvalue({...formvalue, [e.target.name]:e.target.value});
    }
    useEffect( ()=>{
        const getProductList = async()=>{
            var res = await axios.get("http://localhost:8080/produtos");
            if (res?.data) {
                setProductData(res.data.data);
            }
        }
        getProductList();

    },[]);

    return(
        <React.Fragment>
            <div className="container">
                <h5>Painel de venda</h5>

                <Link to="/listsale" className="btn btn-success">Lista de vendas</Link>
                <div class="container text-center">
                    <div class="row align-items-start">
                        {
                            productData.map((pData, index)=>{
                                return (
                                <div class="col-md-6">
                                    <div class="card">
                                        <div class="card-body">
                                            <h5 class="card-title">{pData.nome}</h5>
                                            <p class="card-text">R$ {pData.valor}</p>
                                            <button class="btn btn-primary" onClick={() => addProducToCart(pData.id)}>Adicionar</button>
                                        </div>
                                    </div>
                                </div>

                                );
                            })
                        }
                    </div>
                </div>
                <h5>Carrinho de compras</h5>
                <p>{JSON.stringify(carrinho)}</p>
            </div>
        </React.Fragment>
    );
}

export default Sale;
