import React, {useEffect, useState} from "react";
import { Link } from "react-router-dom";
import axios from "axios";


function Sale(){

    const [formvalue, setFormvalue] = useState({qtd:"",total:""});
    const [productData, setProductData] = useState([]);
    useEffect( ()=>{
        const getProductList = async()=>{
            var res = await axios.get("http://localhost:8080/produtos");
            if (res?.data) {
                setProductData(res.data.data);
            }
        }
        getProductList();

    },[]);

    const addCart = (id,qtd) =>{
        console.log(id, qtd);
    }

    return(
        <React.Fragment>
            <div className="container">
                <h5>Painel de venda</h5>

                <Link to="/listsale" className="btn btn-success">Lista de vendas</Link>
                <table className="table">
                    <thead>
                        <tr>
                            <th scope="col">#</th>
                            <th scope="col">Nome</th>
                            <th scope="col">Valor Un</th>
                            <th scope="col">Quantidade</th>
                            <th scope="col">Total</th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            productData.map((pData, index)=>{
                                return (
                                <tr>
                                    <th>{pData.id}</th>
                                    <td>{pData.nome}</td>
                                    <td>{pData.valor}</td>
                                    <td>
                                            <input 
                                                type="number"
                                                name="qtd"
                                                productId={pData.id}
                                                value={formvalue.qtd}
                                                onChange={addCart(pData.id,1)}
                                            />
                                    </td>
                                    <td>
                                            <input 
                                                disabled
                                                name="total"
                                                productId={pData.id}
                                                value={formvalue.total}
                                            />
                                    </td>
                                </tr>
                                );
                            })
                        }

                    </tbody>
                </table>

            </div>
        </React.Fragment>
    );
}

export default Sale;
