import React, {useEffect, useState} from "react";
import { Link } from "react-router-dom";
import axios from "axios";


function Sale(){

    const [formvalue, setFormvalue] = useState({qtd:'',total:""});
    const [productData, setProductData] = useState([]);
    const handleInput =(e)=>{
        setFormvalue({...formvalue, [e.target.name]:e.target.value});
    }
    useEffect( ()=>{
        const getProductList = async()=>{
            var res = await axios.get("http://localhost:8080/produtos");
            if (res?.data) {
                setProductData(res.data.data);
            }
        }
        getProductList();

    },[]);

    return(
        <React.Fragment>
            <div className="container">
                <h5>Painel de venda</h5>

                <Link to="/listsale" className="btn btn-success">Lista de vendas</Link>
                <div class="container text-center">
                    <div class="row align-items-start">
                    {
                    productData.map((pData, index)=>{
                        return (
                        <div class="col-md-6">
                            <div class="card">
                                <div class="card-body">
                                    <h5 class="card-title">{pData.nome}</h5>
                                    <p class="card-text">{pData.valor}</p>
                                    <a href="#" class="btn btn-primary">Adicionar</a>
                                </div>
                            </div>
                        </div>

                        );
                    })
                }

                </div>
                </div>
                <table className="table">
                    <thead>
                        <tr>
                            <th scope="col">#</th>
                            <th scope="col">Nome</th>
                            <th scope="col">Valor Un</th>
                            <th scope="col">Quantidade</th>
                            <th scope="col">Total</th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            productData.map((pData, index)=>{
                                return (
                                <tr>
                                    <th>{pData.id}</th>
                                    <td>{pData.nome}</td>
                                    <td>{pData.valor}</td>
                                    <td>
                                            <input 
                                                type="number"
                                                name="qtd"
                                                value={formvalue.qtd}
                                                onChange={handleInput}
                                            />
                                    </td>
                                    <td>
                                            <input 
                                                disabled
                                                name="total"
                                                value={formvalue.total}
                                            />
                                    </td>
                                </tr>
                                );
                            })
                        }

                    </tbody>
                </table>

            </div>
        </React.Fragment>
    );
}

export default Sale;
