import axios from "axios";
import React, {useState} from "react";
import { useNavigate } from "react-router-dom";

function AddProduct(){

    const navigate = useNavigate();
    const [formvalue, setFormvalue] = useState({nome:'',valor:''});
    const handleInput =(e)=>{
        setFormvalue({...formvalue, [e.target.name]:e.target.value});
    }

    const handleSubmit =async(e)=>{
        e.preventDefault();
        //console.log(formvalue);
        const formData = {
            nome:formvalue.nome,
            valor:formvalue.valor
        };
        const res = await axios.post("http://localhost:8000/api/produto",formData);

        if(res.data.success){
            navigate('/productlist')
        }
    }

    return(
        <React.Fragment>
        <div className="container">
            <h5>Adicionar Produto</h5>
            <form onSubmit={handleSubmit}>
                <div className="mb-3">
                    <label htmlFor="exampleInputEmail1" className="form-label">Nome do produto:</label>
                    <input type="text" name="nome" className="form-control" id="nome" value={formvalue.nome} onChange={handleInput}/>
                </div>
                <div className="mb-3">
                    <label htmlFor="exampleInputPassword1" className="form-label">Valor:</label>
                    <input type="text" name="valor" className="form-control" id="valor"value={formvalue.valor} onChange={handleInput}/>
                </div>
                <button type="submit" className="btn btn-primary">Submit</button>
            </form>
        </div>
        </React.Fragment>
    );
}

export default AddProduct;
